<script>
    export default {
        data() {
            return {
                isShow1: true,
            }
        },
        mounted() {
            const vh = window.innerHeight / 100
            document.documentElement.style.setProperty('--vh',`${vh}px`)
        },
        methods: {
            navigateWhitePage(isShowWhitePage) {
                const scrollElement = document.getElementById('scrollSectionID')
                
                if(isShowWhitePage) {
                    this.isShow1 = !this.isShow1
                    this.$nextTick(()=> {
                        scrollElement.scrollTop = 0
                    })
                    return 
                } else {
                    scrollElement.style.overflow = 'hidden' // add this line
                    this.isShow1 = !this.isShow1
                    this.$nextTick(()=> {
                        scrollElement.scrollTop = 0
                        scrollElement.style.overflow = '' // add this line
                    })
                }
            }
        }
    }
</script>

<style scoped>
.scrollSection{
    height: calc( var(--vh,1vh)*100 - 100px - 32px - 64px);
    overflow: auto;
}

.bottomBtnStyle{
    position: fixed; 
    bottom: 0;
    height: 100px; 
    width: calc(100vw - 8px);
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin-left: -4px;
}

.bottomBtnStyle > div{
    line-height: 50px;
}

.whitePageBtn{
    flex:1;
    background-color: rgb(72, 35, 69);
    color: white;
    width: 100%;
    text-align: center;
    border: 1px solid black;
    border-bottom: none;
}

.normalPageBtn{
    flex:1;
    width: 100%;
    text-align: center;
    border: 1px solid black;
}

.normalPageBtn:hover,
.whitePageBtn:hover{
    background-color: rgb(183, 147, 218);
    color: white;
}
</style>